// src/components/books/BookList.js
import React, { useState } from "react";
import "../../styles/BookList.css";

function BookList() {
  const [books, setBooks] = useState([
    { id: 1, title: "Harry Potter", author: "J.K. Rowling", available: true },
    {
      id: 2,
      title: "Yüzüklerin Efendisi",
      author: "J.R.R. Tolkien",
      available: true,
    },
    { id: 3, title: "Suç ve Ceza", author: "Dostoyevski", available: false },
  ]);

  const [editingBook, setEditingBook] = useState(null);

  const handleDelete = (id) => {
    if (window.confirm("Bu kitabı silmek istediğinizden emin misiniz?")) {
      setBooks(books.filter((book) => book.id !== id));
    }
  };

  const handleEdit = (book) => {
    setEditingBook(book);
  };

  const handleUpdate = (e) => {
    e.preventDefault();
    setBooks(
      books.map((book) => (book.id === editingBook.id ? editingBook : book))
    );
    setEditingBook(null);
  };

  return (
    <div className="book-list">
      <h2>Kitap Listesi</h2>
      <div className="books-grid">
        {books.map((book) => (
          <div key={book.id} className="book-card">
            {editingBook && editingBook.id === book.id ? (
              <form onSubmit={handleUpdate} className="edit-form">
                <input
                  type="text"
                  value={editingBook.title}
                  onChange={(e) =>
                    setEditingBook({
                      ...editingBook,
                      title: e.target.value,
                    })
                  }
                />
                <input
                  type="text"
                  value={editingBook.author}
                  onChange={(e) =>
                    setEditingBook({
                      ...editingBook,
                      author: e.target.value,
                    })
                  }
                />
                <button type="submit">Kaydet</button>
                <button type="button" onClick={() => setEditingBook(null)}>
                  İptal
                </button>
              </form>
            ) : (
              <>
                <h3>{book.title}</h3>
                <p>Yazar: {book.author}</p>
                <p>Durum: {book.available ? "Mevcut" : "Ödünç Alındı"}</p>
                <div className="book-actions">
                  <button onClick={() => handleEdit(book)}>Düzenle</button>
                  <button onClick={() => handleDelete(book.id)}>Sil</button>
                </div>
              </>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default BookList;
