import React, { useState, useEffect } from "react";
import { bookService } from "../../services/api";
import "../../styles/BookList.css";

function BookList({ user }) {
  const [books, setBooks] = useState([]);
  const [editingBook, setEditingBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadBooks();
  }, []);

  const loadBooks = async () => {
    try {
      setLoading(true);
      setError(null);
      const data = await bookService.getAllBooks();
      setBooks(data);
    } catch (err) {
      console.error("Kitap yükleme hatası:", err);
      setError("Kitaplar yüklenirken bir hata oluştu: " + err.message);
    } finally {
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    try {
      await bookService.deleteBook(id);
      setBooks(books.filter((book) => book.id !== id));
    } catch (err) {
      console.error("Silme hatası:", err);
      setError("Kitap silinirken bir hata oluştu: " + err.message);
    }
  };

  const renderBookActions = (book) => {
    if (!user) return null;

    if (user.role === "admin") {
      return (
        <div className="book-actions">
          <button onClick={() => setEditingBook(book)}>Düzenle</button>
          <button onClick={() => handleDelete(book.id)}>Sil</button>
        </div>
      );
    }

    return (
      <div className="book-actions">
        <button className="borrow-button" onClick={() => handleBorrow(book.id)}>
          Ödünç Al
        </button>
      </div>
    );
  };

  const renderBooks = () => {
    return (
      <div className="books-grid">
        {books.map((book) => (
          <div key={book.id} className="book-card">
            <h3>{book.title}</h3>
            <p>Yazar: {book.author}</p>
            <p>ISBN: {book.isbn}</p>
            <p>Stok: {book.quantity}</p>
            {renderBookActions(book)}
          </div>
        ))}
      </div>
    );
  };

  if (loading) return <div>Yükleniyor...</div>;
  if (error)
    return (
      <div className="error">
        <p>{error}</p>
        <button onClick={loadBooks}>Tekrar Dene</button>
      </div>
    );

  return (
    <div className="book-list">
      <h2>Kitap Listesi</h2>
      {books.length === 0 ? <p>Henüz kitap bulunmamaktadır.</p> : renderBooks()}
    </div>
  );
}

export default BookList;
