// src/components/books/BookList.js
import React, { useState, useEffect } from "react";
import { bookService } from "../../services/api";
import "../../styles/BookList.css";

function BookList() {
  const [books, setBooks] = useState([]);
  const [editingBook, setEditingBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    loadBooks();
  }, []);

  const loadBooks = async () => {
    try {
      const data = await bookService.getAllBooks();
      setBooks(data);
      setLoading(false);
    } catch (err) {
      setError("Kitaplar yüklenirken bir hata oluştu");
      setLoading(false);
    }
  };

  const handleDelete = async (id) => {
    if (window.confirm("Bu kitabı silmek istediğinizden emin misiniz?")) {
      try {
        await bookService.deleteBook(id);
        setBooks(books.filter((book) => book.id !== id));
      } catch (err) {
        setError("Kitap silinirken bir hata oluştu");
      }
    }
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const updatedBook = await bookService.updateBook(
        editingBook.id,
        editingBook
      );
      setBooks(
        books.map((book) => (book.id === updatedBook.id ? updatedBook : book))
      );
      setEditingBook(null);
    } catch (err) {
      setError("Kitap güncellenirken bir hata oluştu");
    }
  };

  if (loading) return <div>Yükleniyor...</div>;
  if (error) return <div className="error">{error}</div>;

  // ... geri kalan render kodu aynı ...
}

export default BookList;
