import React, { useState } from "react";
import "../../styles/Register.css";

function Register() {
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    birthDate: "",
    nationalId: "",
    phone: "",
    email: "",
    password: "",
    confirmPassword: "",
    termsAccepted: false,
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === "checkbox" ? checked : value,
    });
  };

  const validatePassword = (password) => {
    // Şifre güvenlik kriterleri: en az 8 karakter, bir büyük harf, bir küçük harf, bir rakam
    const passwordRegex = /^(?=.*[a-z])(?=.*[A-Z])(?=.*\d)[A-Za-z\d]{8,}$/;
    return passwordRegex.test(password);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    let newErrors = {}; // Şifreler eşleşiyor mu kontrolü

    if (formData.password !== formData.confirmPassword) {
      newErrors.confirmPassword = "Şifreler eşleşmiyor.";
    } // Şifre kriterleri kontrolü

    if (!validatePassword(formData.password)) {
      newErrors.password =
        "Şifre en az 8 karakter, bir büyük harf, bir küçük harf ve bir rakam içermelidir.";
    } // Kullanıcı bilgilendirme formu onayı kontrolü

    if (!formData.termsAccepted) {
      newErrors.termsAccepted =
        "Üyelik için Kullanıcı Bilgilendirme Onay Formunu kabul etmelisiniz!";
    }

    setErrors(newErrors); // Eğer hata yoksa işlemi devam ettir

    if (Object.keys(newErrors).length === 0) {
      console.log("Kayıt bilgileri:", formData);
      alert("Kayıt başarılı!"); // Burada API'ye gönderim yapılabilir
    }
  };

  return (
    <div className="register-container">
            
      <form className="register-form" onSubmit={handleSubmit}>
                <h2>Kayıt Ol</h2>
                
        <div className="form-group">
                    <label>Ad</label>
                    
          <input
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            required
          />
                  
        </div>
                
        <div className="form-group">
                    <label>Soyad</label>
                    
          <input
            type="text"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            required
          />
                  
        </div>
                
        <div className="form-group">
                    <label>Doğum Tarihi</label>
                    
          <input
            type="date"
            name="birthDate"
            value={formData.birthDate}
            onChange={handleChange}
            required
          />
                  
        </div>
                
        <div className="form-group">
                    <label>TC Kimlik</label>
                    
          <input
            type="text"
            name="nationalId"
            value={formData.nationalId}
            onChange={handleChange}
            required
            pattern="\d{11}"
            title="TC Kimlik numarası 11 rakamdan oluşmalıdır."
          />
                  
        </div>
                
        <div className="form-group">
                    <label>Telefon Numarası</label>
                    
          <input
            type="tel"
            name="phone"
            value={formData.phone}
            onChange={handleChange}
            required
            pattern="\d{10}"
            title="Telefon numarası 10 rakamdan oluşmalıdır. (Örnek: 5551234567)"
          />
                  
        </div>
                
        <div className="form-group">
                    <label>Email</label>
                    
          <input
            type="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
                  
        </div>
                
        <div className="form-group">
                    <label>Şifre</label>
                    
          <input
            type="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
          />
                    
          {errors.password && (
            <small className="error">{errors.password}</small>
          )}
                  
        </div>
                
        <div className="form-group">
                    <label>Şifre (Tekrar)</label>
                    
          <input
            type="password"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            required
          />
                    
          {errors.confirmPassword && (
            <small className="error">{errors.confirmPassword}</small>
          )}
                  
        </div>
                
        <div className="form-group">
                    
          <label>
                        
            <input
              type="checkbox"
              name="termsAccepted"
              checked={formData.termsAccepted}
              onChange={handleChange}
            />
                        Kullanıcı Bilgilendirme Onay Formu'nu okudum ve kabul
            ediyorum.             
            <a
              href="https://example.com/terms" // Burada Kullanıcı Bilgilendirme linkini verin
              target="_blank" // Yeni pencerede açılmasını sağlarız
              rel="noopener noreferrer"
            >
                            Buraya tıklayarak formu görüntüleyebilirsiniz.
                          
            </a>
                      
          </label>
                    
          {errors.termsAccepted && (
            <small className="error">{errors.termsAccepted}</small>
          )}
                  
        </div>
                <button type="submit">Kayıt Ol</button>
              
      </form>
          
    </div>
  );
}

export default Register;
